// Card.jsx
import React from "react";

const Card = ({ attributes, onToggle, playerName }) => {
  const { revealed, health, profession, hobby, age, phobia } = attributes;

  const handleToggle = (key) => {
    onToggle(key);
  };

  return (
    <div className="card">
      <h2>{playerName}</h2>
      <p onClick={() => handleToggle("health")}>Здоровье: {revealed.health ? health : "---"}</p>
      <p onClick={() => handleToggle("profession")}>Профессия: {revealed.profession ? profession : "---"}</p>
      <p onClick={() => handleToggle("hobby")}>Хобби: {revealed.hobby ? hobby : "---"}</p>
      <p onClick={() => handleToggle("age")}>Возраст: {revealed.age ? age : "---"}</p>
      <p onClick={() => handleToggle("phobia")}>Фобия: {revealed.phobia ? phobia : "---"}</p>
    </div>
  );
};

export default Card;
