import React, { useState } from "react";
import Card from "./Card";
import generateRandomAttributes from "./randomAttributes";

const App = () => {
  const [cards, setCards] = useState(
    Array.from({ length: 12 }, () => ({ ...generateRandomAttributes(), revealed: false }))
  );

  const toggleReveal = (index) => {
    setCards((prevCards) =>
      prevCards.map((card, i) =>
        i === index ? { ...card, revealed: !card.revealed } : card
      )
    );
  };

  return (
    <div className="app">
      <h1>Игра "Бункер"</h1>
      <div className="cards-container">
        {cards.map((card, index) => (
          <Card
            key={index}
            attributes={card}
            onToggle={() => toggleReveal(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
