// App.jsx
import React, { useState } from "react";
import Card from "./Card";
import generateRandomAttributes from "./randomAttributes";
import styles from "./App.css"

const App = () => {
  const [cards, setCards] = useState(
    Array.from({ length: 12 }, (_, index) => ({
      ...generateRandomAttributes(),
      revealed: {
        health: false,
        profession: false,
        hobby: false,
        age: false,
        phobia: false,
      },
      playerName: `Игрок ${index + 1}`,
    }))
  );

  const toggleReveal = (index, key) => {
    setCards((prevCards) =>
      prevCards.map((card, i) =>
        i === index
          ? {
              ...card,
              revealed: { ...card.revealed, [key]: !card.revealed[key] },
            }
          : card
      )
    );
  };

  const updatePlayerName = (index, newName) => {
    setCards((prevCards) =>
      prevCards.map((card, i) =>
        i === index ? { ...card, playerName: newName } : card
      )
    );
  };

  return (
    <div className="app">
      <h1>Игра "Бункер"</h1>
      <div className="cards-container">
        {cards.map((card, index) => (
          <Card
            key={index}
            attributes={card}
            onToggle={(key) => toggleReveal(index, key)}
            playerName={card.playerName}
            onNameChange={(newName) => updatePlayerName(index, newName)}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
